import React from 'react';
import Pokemon from './Pokemon';

class Pokedex extends React.Component {
  constructor() {
    super();
    this.state = {
      index: 0,
      filter: 'Fire',
      pokemonsFiltered: [
        {
          id: 25,
          name: 'Pikachu',
          type: 'Electric',
          averageWeight: {
            value: '6.0',
            measurementUnit: 'kg',
          },
          image: 'https://cdn.bulbagarden.net/upload/b/b2/Spr_5b_025_m.png',
          moreInfo:
            'https://bulbapedia.bulbagarden.net/wiki/Pikachu_(Pok%C3%A9mon)',
        },
      ],
    };
  }

  increaseIndex = () => {
    this.setState((estadoAnterior, _props) => ({
      index: estadoAnterior.index + 1,
    }));
  };

  filterPokemon = () => {
    let pokemons = this.props.pokemons;

    pokemons = pokemons.filter((pokemon) => pokemon.type === this.state.filter);

    this.setState({ pokemonsFiltered: pokemons });

    if (this.state.index >= pokemons.length) {
      this.setState({ index: 0 });
    } else {
      this.increaseIndex();
    }
  };

  render() {
    return (
      <div className="pokedex">
        <Pokemon pokemon={this.state.pokemonsFiltered} />
        <button >Pr√≥ximo</button>
      </div>
    );
  }
}

export default Pokedex;
