import React from 'react';
import Pokemon from './Pokemon';

class Pokedex extends React.Component {
  constructor() {
    super();
    this.state = {
      index: 0,
      filter: 'Fire',
      pokemonsFiltered: [
        {
            id: 4,
            name: "Charmander",
            type: 'Fire',
            averageWeight: {
                value: "8.5",
                measurementUnit: "kg"
            },
            image: "https://cdn.bulbagarden.net/upload/0/0a/Spr_5b_004.png",
            moreInfo: "https://bulbapedia.bulbagarden.net/wiki/Charmander_(Pok%C3%A9mon)"
        },
      ],
    };
  }

  increaseIndex = () => {
    this.setState((estadoAnterior, _props) => ({
      index: estadoAnterior.index + 1,
    }));
  };

  filterPokemon = () => {
    let pokemons = this.props.pokemons;

    pokemons = pokemons.filter((pokemon) => pokemon.type === this.state.filter);

    this.setState({ pokemonsFiltered: pokemons });

    if (this.state.index >= pokemons.length) {
      this.setState({ index: 0 });
    } else {
      this.increaseIndex();
    }
  };

  render() {
    return (
      <div className="pokedex">
        <Pokemon pokemon={this.state.pokemonsFiltered[this.state.index]} />
        <button onClick={this.filterPokemon}>Pr√≥ximo</button>
      </div>
    );
  }
}

export default Pokedex;
