import React from 'react';
import Pokemon from './Pokemon';

class Pokedex extends React.Component {
  constructor() {
    super();
    this.state = {
      index: 0,
      filter: 'Fire',
      sizeFilter: 2,
    };
  }

  increaseIndex = () => {
    if (this.state.index < this.state.sizeFilter - 1) {
      this.setState((estadoAnterior, _props) => ({
        index: estadoAnterior.index + 1,
      }));
    } else {
      this.setState({ index: 0 });
    }
  };

  filterPokemons = (param) => {
    let pokemons = this.props.pokemons.filter((pokemon) => pokemon.type === this.state.filter);

    this.setState({ filter: param });
    this.setState({ sizeFilter: pokemons.length });
  };

  render() {
    let pokemons = this.props.pokemons;

    pokemons = pokemons.filter((pokemon) => pokemon.type === this.state.filter);

    console.log(this);

    return (
      <div className="pokedex">
        <Pokemon pokemon={pokemons[this.state.index]} />
        <button onClick={this.increaseIndex}>Pr√≥ximo</button>
        <button onClick={() => this.filterPokemons('Fire')}>Fire</button>
        <button onClick={() => this.filterPokemons('Psychic')}>Psychic</button>
      </div>
    );
  }
}

export default Pokedex;
